package com.htc.javaexercise.exercise;

public class SeriesLogic2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
				for (int i = 1; i <= 15; i++) {    
					if(i<15)
					{
						System.out.print("1/"+i+",");
					}
					if(i==15)
					{
						System.out.print("1/"+i);
					}
			
				}
				}
			}


	
